  const colorChannel = inputs[1].metadata.colorChannel;
  const code = `${outputs[1]} = K.feature.gftt_response(${inputs[1]}, ${inputs[2]}, ${inputs[3]})
 ${outputs[1]} = {
   'value': ${outputs[1]},
  'dataType': 'torch.tensor',
  'metadata': {
    'colorChannel': '${colorChannel}',
    'channelOrder': 'channelFirst',
    'isMiniBatched': True,
    'intensityRange': '0-1',
    'device': ${input[1]}.device == 'cpu'? 'cpu' : 'gpu'
  }
}
${outputs[0]}`;
  return code;
}

