Equalize.codeGenerator:
function code(inputs, outputs, node, generator) {
  const colorChannel = inputs[1].metadata.colorChannel;
  const code = `${outputs[1]} = K.enhance.equalize(${inputs[1]})
  ${outputs[1]} = {
   'value': ${outputs[1]},
  'dataType': 'torch.tensor',
  'metadata': {
    'colorChannel': '${colorChannel}',
    'channelOrder': 'channelFirst',
    'isMiniBatched': False,
    'intensityRange': '0-1',
    'device': ${input[1]}.device == 'cpu'? 'cpu' : 'gpu'
  }
}
  ${outputs[0]}`;
  return code;
}

